import { Component } from '@angular/core';
import { SocketService } from 'src/app/services/socket.service'; 

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent {

  message : string = '';

  constructor(
		private socketService: SocketService 
	) { }

  onSendMessage(message : string) {
    // this.socketService.joinRoom(roomName);
    // this.createDiv(roomName);
    this.message = '';
  }

}
<div class="container">

    <div class="flex column">

        <h1 class="title">Chat Nº 1</h1>

        <div class="messages-container">            
            <!-- <div  *ngFor="let room of rooms" id="room-{{room.name}}">{{room.name}}</div> -->
        </div>

        <div class="row send-message">
            <input type="text" autocomplete="off" placeholder="Message..." id="message-input" [(ngModel)]="message">
            <button (click)="onSendMessage(message)" type="button" id="message-button">Send</button>
        </div>

        <a routerLink="/" routerLinkActive="active" ariaCurrentWhenActive="page">Go Back</a>

    </div>


</div>
