@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

}
<div class="container">

    <div class="flex column">

      <h1 class="title">Welcome to Sockets Io Chat Rooms</h1>

      <div class="row create-room">
