import { Injectable } from '@angular/core';
import { Socket } from 'ngx-socket-io';  

@Injectable({
  providedIn: 'root'
})
export class SocketService {

  constructor(private socket: Socket) { }

  // emit event
	fetchRooms() {
		this.socket.emit("fetch-rooms");
	} 

	// listen event
	onFetchRooms() {
		return this.socket.fromEvent<string[]>("update-rooms");
	}
  //equivalent
  //roomNames = this.socket.fromEvent<string[]>("update-rooms");

  //teste
  teste() {
    return this.socket.emit('teste');
  }

  joinRoom(room : string) {
    this.socket.emit("create-and-join-room", room, (message: any) => {
      console.log(message);
    });
  }

  sendMessage(message : string, room : string) {
    this.socket.emit("send-message", message, room);
    console.log(message);
  }

  // onUpdateRooms() {
  //   return this.socket.fromEvent("update-rooms");
  // }

}
