import { Component } from '@angular/core';
import { SocketService } from 'src/app/services/socket.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  userName : string = '';  

  constructor(
		private socketService: SocketService
	) { }

  onCreateUser(userName : string) {    
    this.userName = '';
    // this.router.navigate(['/room', roomName]);
    // this.socketService.createRoom(roomName);
  }

}
<div class="container">

    <div class="flex column">

      <h1 class="title">Welcome to Sockets Io Chat Rooms</h1>

      <div class="row create-user">
        <input type="text" autocomplete="off" placeholder="User name..." id="user-input" [(ngModel)]="userName">
        <button (click)="onCreateUser(userName)" type="button" id="user-button">Create User</button>
      </div>

    </div>
</div>
